const button=document.getElementById("toggle-mode"),root=document.documentElement,darkColors={"--heading-color":"#fff","--background-color":"#282b36","--button-background":"#fff","--button-color":"#282b36"},lightColors={"--heading-color":"#282b36","--background-color":"#fff","--button-background":"#282b36","--button-color":"#fff"},setColors=(o,t)=>{button.textContent=o[0].toUpperCase()+o.substring(1);for(const[o,r]of Object.entries(t))root.style.setProperty(o,r)};button.addEventListener("click",(()=>{root.classList.toggle("dark-mode"),root.classList.contains("dark-mode")?setColors("light",darkColors):setColors("dark",lightColors)}));